import { useEffect, useState } from 'react';
import { Link } from 'wouter';
import { Achievement, ACHIEVEMENTS } from '@shared/schema';
import * as LucideIcons from 'lucide-react';
import { Button } from '@/components/ui/button';
import { ArrowLeft, Lock } from 'lucide-react';

export default function Achievements() {
  const [playerProgress, setPlayerProgress] = useState<any>(null);
  const [playerId, setPlayerId] = useState<string | null>(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const id = urlParams.get('playerId');
    setPlayerId(id);

    if (id) {
      const stored = localStorage.getItem('ratinho-player-progress');
      if (stored) {
        const allProgress = JSON.parse(stored);
        setPlayerProgress(allProgress[id]);
      }
    }
  }, []);

  const unlockedIds = new Set(playerProgress?.achievements?.map((a: Achievement) => a.id) || []);

  const categories = [
    { id: 'collection', name: 'Coleção', color: 'from-blue-500 to-cyan-500' },
    { id: 'accuracy', name: 'Precisão', color: 'from-green-500 to-emerald-500' },
    { id: 'difficulty', name: 'Dificuldade', color: 'from-purple-500 to-pink-500' },
    { id: 'speed', name: 'Velocidade', color: 'from-orange-500 to-red-500' },
    { id: 'social', name: 'Social', color: 'from-yellow-500 to-amber-500' },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary/10 via-background to-accent/10 p-6">
      <div className="max-w-5xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <Link href={`/?playerId=${playerId}`}>
            <Button variant="outline" size="icon" data-testid="button-back">
              <ArrowLeft className="w-5 h-5" />
            </Button>
          </Link>
          <h1 className="text-4xl font-extrabold text-foreground" style={{ fontFamily: 'Fredoka, sans-serif' }}>
            Conquistas
          </h1>
          <div className="w-10" />
        </div>

        {playerProgress && (
          <div className="bg-card rounded-xl p-6 mb-8 border border-border">
            <h2 className="text-xl font-bold mb-4">Progresso Geral</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center">
                <p className="text-3xl font-bold text-primary">{unlockedIds.size}</p>
                <p className="text-sm text-muted-foreground">Conquistas</p>
              </div>
              <div className="text-center">
                <p className="text-3xl font-bold text-accent">{playerProgress.totalItemsCollected || 0}</p>
                <p className="text-sm text-muted-foreground">Itens Coletados</p>
              </div>
              <div className="text-center">
                <p className="text-3xl font-bold text-primary">{playerProgress.totalGamesPlayed || 0}</p>
                <p className="text-sm text-muted-foreground">Partidas</p>
              </div>
              <div className="text-center">
                <p className="text-3xl font-bold text-accent">{playerProgress.highestScore || 0}</p>
                <p className="text-sm text-muted-foreground">Maior Pontuação</p>
              </div>
            </div>
          </div>
        )}

        {categories.map(category => {
          const categoryAchievements = ACHIEVEMENTS.filter(a => a.category === category.id);

          return (
            <div key={category.id} className="mb-8">
              <h2 className="text-2xl font-bold mb-4 flex items-center gap-2">
                <div className={`w-1 h-8 rounded bg-gradient-to-b ${category.color}`} />
                {category.name}
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {categoryAchievements.map(achievement => {
                  const isUnlocked = unlockedIds.has(achievement.id);
                  const IconComponent = (LucideIcons as any)[achievement.icon] || LucideIcons.Award;

                  return (
                    <div
                      key={achievement.id}
                      data-testid={`achievement-${achievement.id}`}
                      className={`relative overflow-hidden rounded-lg border-2 transition-all ${
                        isUnlocked
                          ? 'border-primary bg-card hover-elevate'
                          : 'border-border bg-muted/50'
                      }`}
                    >
                      <div className="p-4 flex items-start gap-3">
                        <div
                          className={`w-12 h-12 rounded-full flex items-center justify-center flex-shrink-0 ${
                            isUnlocked
                              ? `bg-gradient-to-br ${category.color}`
                              : 'bg-muted'
                          }`}
                        >
                          {isUnlocked ? (
                            <IconComponent className="w-6 h-6 text-white" />
                          ) : (
                            <Lock className="w-6 h-6 text-muted-foreground" />
                          )}
                        </div>
                        <div className="flex-1 min-w-0">
                          <h3
                            className={`font-bold text-sm ${
                              isUnlocked ? 'text-foreground' : 'text-muted-foreground'
                            }`}
                          >
                            {achievement.name}
                          </h3>
                          <p className="text-xs text-muted-foreground mt-1">
                            {achievement.description}
                          </p>
                        </div>
                      </div>
                      {isUnlocked && (
                        <div className={`h-1 w-full bg-gradient-to-r ${category.color}`} />
                      )}
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

client/src/components/FloatingScore.tsx.content:
import { useEffect, useState } from 'react';
import { CheckCircle, XCircle } from 'lucide-react';

interface FloatingScoreProps {
  points: number;
  position: { x: number; y: number };
  success: boolean;
  onComplete: () => void;
}

export function FloatingScore({ points, position, success, onComplete }: FloatingScoreProps) {
  const [opacity, setOpacity] = useState(1);

  useEffect(() => {
    const timer = setTimeout(() => {
      setOpacity(0);
      setTimeout(onComplete, 300);
    }, 1000);

    return () => clearTimeout(timer);
  }, [onComplete]);

  return (
    <div
      data-testid="floating-score"
      className="absolute pointer-events-none z-50 animate-in fade-in slide-in-from-bottom-4 duration-300"
      style={{
        left: `${position.x}px`,
        top: `${position.y}px`,
        transform: 'translate(-50%, -50%)',
        opacity,
        transition: 'all 1s ease-out',
      }}
    >
      <div
        className={`flex items-center gap-2 px-4 py-2 rounded-full font-bold text-xl shadow-lg ${
          success
            ? 'bg-green-500 text-white'
            : 'bg-red-500 text-white'
        }`}
      >
        {success ? (
          <CheckCircle data-testid="icon-success" className="w-5 h-5" />
        ) : (
          <XCircle data-testid="icon-error" className="w-5 h-5" />
        )}
        <span data-testid="text-feedback">{success ? `+${points}` : 'Ops!'}</span>
      </div>
    </div>
  );
}

client/src/components/ui/carousel.tsx.content:
import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return
      }

      setCanScrollPrev(api.canScrollPrev())
      setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
      api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
      api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault()
          scrollPrev()
        } else if (event.key === "ArrowRight") {
          event.preventDefault()
          scrollNext()
        }
      },
      [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
      if (!api || !setApi) {
        return
      }

      setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
      if (!api) {
        return
      }

      onSelect(api)
      api.on("reInit", onSelect)
      api.on("select", onSelect)

      return () => {
        api?.off("select", onSelect)
      }
    }, [api, onSelect])

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api: api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          ref={ref}
          onKeyDownCapture={handleKeyDown}
          className={cn("relative", className)}
          role="region"
          aria-roledescription="carousel"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    )
  }
)
Carousel.displayName = "Carousel"

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div ref={carouselRef} className="overflow-hidden">
      <div
        ref={ref}
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
})
CarouselContent.displayName = "CarouselContent"

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel()

  return (
    <div
      ref={ref}
      role="group"
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
})
CarouselItem.displayName = "CarouselItem"

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute  h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-left-12 top-1/2 -translate-y-1/2"
          : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      {...props}
    >
      <ArrowLeft className="h-4 w-4" />
      <span className="sr-only">Previous slide</span>
    </Button>
  )
})
CarouselPrevious.displayName = "CarouselPrevious"

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-right-12 top-1/2 -translate-y-1/2"
          : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      {...props}
    >
      <ArrowRight className="h-4 w-4" />
      <span className="sr-only">Next slide</span>
    </Button>
  )
})
CarouselNext.displayName = "CarouselNext"

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
}

